import React from 'react';
import { Box, Typography, Grid } from '@mui/material';
import { useTheme } from '@mui/material/styles';

interface TodayForecastProps {
  realFeel?: number;
  rainChance?: number;
  windSpeed?: number;
  uvIndex?: number;
  condition?: string;
}

const TodayForecast: React.FC<TodayForecastProps> = ({ realFeel, rainChance, windSpeed, uvIndex, condition }) => {
  const theme = useTheme();

  const styles = {
    container: {
      padding: '20px',
      backgroundColor: '#1E2A38', // Dark Blue Background
      color: '#FFFFFF', // White Text
      borderRadius: '10px',
    },
    title: {
      borderBottom: `2px solid ${theme.palette.primary.main}`,
      paddingBottom: '10px',
      marginBottom: '20px',
    },
    forecastItem: {
      textAlign: 'center',
    },
    time: {
      color: '#BCC5D3', // Light Gray for time
    },
    temp: {
      fontSize: '1.2rem',
      fontWeight: 'bold',
    },
  };

  return (
    <Box sx={styles.container}>
      <Typography variant="h5" sx={styles.title}>Today's Forecast</Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} sx={styles.forecastItem}>
          <Typography variant="body2">Real Feel: {realFeel}Â°C</Typography>
        </Grid>
        <Grid item xs={12} sx={styles.forecastItem}>
          <Typography variant="body2">Rain Chance: {rainChance}%</Typography>
        </Grid>
        <Grid item xs={12} sx={styles.forecastItem}>
          <Typography variant="body2">Wind Speed: {windSpeed} km/h</Typography>
        </Grid>
        <Grid item xs={12} sx={styles.forecastItem}>
          <Typography variant="body2">UV Index: {uvIndex}</Typography>
        </Grid>
        <Grid item xs={12} sx={styles.forecastItem}>
          <Typography variant="body2">Condition: {condition}</Typography>
        </Grid>
      </Grid>
    </Box>
  );
};

export default TodayForecast;
