import React, { useEffect, useState } from 'react';
import { Grid, Box } from '@mui/material';
import Sidebar from '../components/Sidebar';
import Header from '../components/Header';
import WeatherDetails from '../components/WeatherDetails';
import TodayForecast from '../components/TodayForecast';
import SevenDayForecast from '../components/SevenDayForecast';
import Footer from '../components/Footer';
import { useTheme } from '@mui/material/styles';
import config from '../config';

interface WeatherData {
  name: string;
  main: {
    temp: number;
  };
  weather: Array<{
    description: string;
    icon: string;
  }>;
}

const Home: React.FC = () => {
  const [weatherData, setWeatherData] = useState<WeatherData | null>(null);
  const [location, setLocation] = useState({})
  const theme = useTheme();

  const getWeatherApi = async (lat: number, lon: number) => {
    try {
      const url = `${config.apiUrl}?lat=${lat}&lon=${lon}&appid=${config.apiKey}`;
     // console.log('url=', url);
      const response = await fetch(url);
      const result = await response.json();
      setWeatherData(result);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  useEffect(() => {
    //console.log(navigator.geolocation)
    if(navigator.geolocation){
      navigator.geolocation.getCurrentPosition(
        (position)=>{
          const {latitude, longitude} = position.coords
          setLocation({lat: latitude, lon: longitude})
          getWeatherApi(latitude, longitude);
        },
        (error)=>{
          console.error("Error getting location:", error)
          getWeatherApi(28.6139, 77.2090);
        }
      )
    }
    
  }, []);

  console.log('weatherData=', weatherData?.name);

  const styles = {
    themeColor: {
      backgroundColor: '#1E2A38', // Dark Blue Background
      color: '#FFFFFF', // White Text
      minHeight: '100vh',
    },
    mainGrid: {
      padding: '30px 20px 20px 30px',
    },
    sidebar: {
      backgroundColor: '#1E2A38', // Ensure Sidebar has the same dark background
    },
  };

  return (
    <Grid container sx={styles.themeColor}>
      <Grid item xs={2} sx={styles.sidebar}>
        <Sidebar />
      </Grid>
      <Grid item xs={10}>
        <Header />
        <Grid container spacing={2} sx={styles.mainGrid}>
          <Grid item xs={12}>
            <WeatherDetails />
          </Grid>
          <Grid item xs={12} md={6}>
            <TodayForecast />
          </Grid>
          <Grid item xs={12} md={6}>
            <SevenDayForecast />
          </Grid>
        </Grid>
        <Footer />
      </Grid>
    </Grid>
  );
};

export default Home;
